package com.shiro.dh.entity;import javax.persistence.Column;
import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.Table;import javax.persistence.Transient;@Entity@Table(name="t_blog_types")public class BlogTypes implements java.io.Serializable {	private static final long serialVersionUID = -8436758490590285623L;		private long id;	private String name;	private Boolean isUse;		public BlogTypes(){}		@Id	@GeneratedValue 	public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}		@Column(name="name")	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	@Column(name="is_use")	public Boolean getIsUse() {		return isUse;	}	public void setIsUse(Boolean isUse) {		this.isUse = isUse;	}		@Transient	public String getIsUseText(){		if(isUse==null){			return "";		}		return this.isUse?"<font color='green'>启用</font>":"<font color='red'>隐藏</font>";	}	}
